mapper.select:
    select  

    from quality_report   
    where project_no = #{projectNo,jdbcType=VARCHAR}  


	       and type in (2,3)


	       and type = #{type,jdbcType=INTEGER}


    order by createTime desc

select q.id, q.status, q.type, q.pic_url, q.createTime, q.user, q.project_no,q.explain_cause,q.check_explain,q.package_explain, q.spend_time,q.place,q.check_date,q.orders,q.box_number,q.per_qty,q.inventory_qty,q.open_qty,q.inspection_form,u1.user_name as sell_name, q.inspection_path,q.pic_num,q.conclusion ,q.company_name, q.no_check, q.key_size_exceed,q.factory_inspection,p.project_name,u.user_name as purchase_name, t.operate_explain,t.product_file_name from quality_report q left join project p on q.project_no = p.project_no left join user u on p.purchase_id = u.id left join user u1 on p.email_user_id = u1.id left join project_task t on q.id = t.quality_id where 1=1
AND ( (p.project_name like CONCAT('%', #{inputKey}, '%')) OR (q.project_no like CONCAT('%', #{inputKey}, '%')) )
order by q.createTime desc limit #{pageNumber},#{pageSize}
and (q.user = #{userName} OR u1.user_name = #{userName} OR u.user_name = #{userName})
AND ( (p.project_name like CONCAT('%', #{inputKey}, '%')) OR (q.project_no like CONCAT('%', #{inputKey}, '%')) )
order by q.createTime desc limit #{pageNumber},#{pageSize}

select  from quality_report where createTime = (select MAX(createTime) 
  from quality_report  where  1=1

   AND project_no=#{projectNo,jdbcType=VARCHAR}

  )

  AND project_no=#{projectNo,jdbcType=VARCHAR}
